template<int modulus> class modular_hash {
  int length = 0;
  using value_type = Mint<modulus>;
  std::vector<value_type> value;
  std::vector<value_type> power;

 public:
  modular_hash() = default;
  template<typename Array>
  modular_hash(const Array &a, int base, int offset = 0) {
    length = (int) std::size(a);
    assert(length > 0);
    value.resize(length);
    power.resize(length);
    value_type sum = 0;
    for (int i = 0; i < length; i++) {
      assert(a[i] >= offset);
      sum = sum * base + (a[i] - offset);
      value[i] = sum;
    }
    power[0] = 1;
    for (int i = 1; i < length; i++) {
      power[i] = base * power[i - 1];
    }
  }

  value_type operator()(int pos, int count) const {
    assert(0 <= pos and pos < length);
    assert(count > 0);
    return pos == 0 ? value[count - 1]
                    : value[pos + count - 1] - power[count] * value[pos - 1];
  }

  template<typename Array>
  value_type operator()(const Array &a, int base, int offset = 0) const {
    value_type res = 0;
    for (auto x : a) {
      assert(x >= offset);
      res = (res * base + x - offset);
    }
    return res;
  }
};
